import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import InnerNav from "../NavBar/InnerNav";
import { getSessionCookie } from "../auth/sessions";

function TwilioCreds() {
  const [AccountSID, setAccountSID] = useState("");
  const [AuthToken, setAuthToken] = useState("");
  const [errormsg, setErrormsg] = useState("");
  const [currentSID, setCurrentSID] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/updateCreds", {
      method: "GET",
      headers: {
        "x-access-token": getSessionCookie().token,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else throw new Error("error");
      })
      .then((data) => {
        setCurrentSID(data.sid);
      });
  }, []);

  const updateCreds = async (sid, auth) => {
    return fetch("http://localhost:5000/updateCreds", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getSessionCookie().token,
      },
      body: JSON.stringify({
        sid: sid,
        auth: auth,
      }),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        if (data.error) {
          console.log(data.error);
          throw new Error(`error - ${data.error}`);
        } else {
          console.log("creds updated");
          return Promise.resolve();
        }
      })
      .catch((err) => {
        console.log(err);
        return Promise.reject(err);
      });
  };

  return (
    <div id="TwilioCreds">
      <h1>Update Your Twilio Credentials</h1>

      <InnerNav />
      <form>
        <div>
          <label>Twilio Account SID: </label>
          <input
            type="text"
            value={AccountSID}
            onChange={(e) => setAccountSID(e.target.value)}
          />
        </div>
        <div>
          <label>AuthToken: </label>
          <input
            type="password"
            value={AuthToken}
            onChange={(e) => setAuthToken(e.target.value)}
          />
        </div>
        <button
          type="button"
          onClick={() => {
            updateCreds(AccountSID, AuthToken)
              .then(() => navigate("/Console"))
              .catch((err) => setErrormsg(err.message));
          }}
        >
          Submit
        </button>
      </form>
      <p>{errormsg}</p>
    </div>
  );
}

export default TwilioCreds;
