import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import InnerNav from "../NavBar/InnerNav";
import { getSessionCookie } from "../auth/sessions";

function Numbers() {
  // have to initialize an array or it breaks... fix this
  const [numbers, setNumbers] = useState([{ nickname: "b", number: "5" }]);
  const [nickname, setNickname] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [errormsg, setErrormsg] = useState("");
  const navigate = useNavigate();

  // On Load fetch the users numbers loaded in the db
  useEffect(() => {
    fetch("http://localhost:5000/numbers", {
      method: "GET",
      headers: {
        "x-access-token": getSessionCookie().token,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else throw new Error("error");
      })
      .then((data) => {
        setNumbers(data.numbers);
        console.log(data);
      });
  }, []);

  const addNewNum = async (nick, num) => {
    return fetch("http://localhost:5000/numbers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getSessionCookie().token,
      },
      body: JSON.stringify({
        nickname: nick,
        number: num,
      }),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        if (data.error) {
          console.log(data.error);
          throw new Error(`error - ${data.error}`);
        } else {
          console.log("creds updated");
          return Promise.resolve();
        }
      })
      .catch((err) => {
        console.log(err);
        return Promise.reject(err);
      });
  };

  return (
    <div id="Numbers">
      <h1>Update Your Twilio Credentials</h1>
      <div className="tableDiv">
        <table id="demTable">
          <thead>
            <tr>
              <th>Number Nickname</th>
              <th>Phone Number</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {numbers.map((num) => {
              return (
                <tr>
                  <td>{num.nickname}</td>
                  <td>{num.number}</td>
                  <td>Edit</td>
                  <td>Delete</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <InnerNav />
      <form>
        <div>
          <label>Nickname: </label>
          <input
            type="text"
            value={nickname}
            onChange={(e) => setNickname(e.target.value)}
          />
        </div>
        <div>
          <label>Phone Number: </label>
          <input
            type="text"
            value={newNumber}
            onChange={(e) => setNewNumber(e.target.value)}
          />
        </div>
        <button
          type="button"
          onClick={() => {
            addNewNum(nickname, newNumber)
              .then(() => navigate("/Console"))
              .catch((err) => setErrormsg(err.message));
          }}
        >
          Submit
        </button>
      </form>
      <p>{errormsg}</p>
    </div>
  );
}

export default Numbers;
