import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import InnerNav from "../NavBar/InnerNav";

function TwilioCreds() {
  const [AccountSID, setAccountSID] = useState("");
  const [AuthToken, setAuthToken] = useState("");
  const [errormsg, setErrormsg] = useState("");
  const navigate = useNavigate();

  const updateCreds = async (email, password) => {
    return fetch("http://localhost:5000/updateCreds", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        sid: AccountSID,
        auth: AuthToken,
      }),
    }).then();
  };

  return (
    <div id="TwilioCreds">
      <h1>Update Your Twilio Credentials</h1>

      <InnerNav />
      <form>
        <div>
          <label>Twilio Account SID: </label>
          <input
            type="text"
            value={AccountSID}
            onChange={(e) => setAccountSID(e.target.value)}
          />
        </div>
        <div>
          <label>AuthToken: </label>
          <input
            type="password"
            value={AuthToken}
            onChange={(e) => setAuthToken(e.target.value)}
          />
        </div>
        <button
          type="button"
          onClick={() => {
            console
              .log(AccountSID, AuthToken)
              .then(() => navigate("/Console"))
              .catch((err) => setErrormsg(err.message));
          }}
        >
          Submit
        </button>
      </form>
      <p>{errormsg}</p>
    </div>
  );
}

export default TwilioCreds;
