// /src/hooks/useAuth.tsx
import React, { useState, createContext, useContext, useEffect } from "react";
import { getSessionCookie, setSessionCookie, dropCookie } from "./sessions";
import { useNavigate } from "react-router-dom";

// Create the context
const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [authed, setAuthed] = useState(false);
  // Store new value to indicate the call has not finished. Default to true
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Runs once when the component first mounts
  useEffect(() => {
    fetch("http://localhost:5000/@me", {
      method: "GET",
      headers: {
        "x-access-token": getSessionCookie().token,
      },
    })
      .then((response) => {
        if (response.status == 200) {
          return true;
        } else return false;
      })
      .then((activeUser) => {
        if (activeUser) {
          setAuthed(true);
          setLoading(false);
          return true;
        } else {
          setAuthed(false);
          setLoading(false);
          return false;
        }
      });
  }, []);

  const login = (email, password) => {
    try {
      fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          password: password,
          email: email,
        }),
      })
        .then((response) => {
          //if (response.status == 200) {
          //setRedirect(true);
          //}
          return response.json();
        })
        .then((data) => {
          if (data.error) {
            console.log(data.error);
            return data;
          } else {
            console.log("logged in");
            setSessionCookie(JSON.stringify(data));
            setAuthed(true);
            useNavigate("/Console");
            return Promise.resolve();
          }
        });
    } catch (error) {
      console.log(error);
      if (error.status === 401) {
        alert("Invalid credentials");
      }
    }
  };

  const logout = () => {
    dropCookie();
    console.log("The User has logged out");
    setAuthed(false);
  };

  return (
    // Using the provider so that ANY component in our application can
    // use the values that we are sending.
    <AuthContext.Provider value={{ authed, setAuthed, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Finally creating the custom hook
export const useAuth = () => useContext(AuthContext);
